mapper.update:
UPDATE product SET
clearance_activation_date = #{activationDate},
clearance_deactivation_date = #{deactivationDate}
WHERE id = #{id}

mapper.select:
SELECT parent_item_id,child_item_id from item_relation WHERE child_item_id in
#{item}
AND item_relation_type ='PP_PRODUCT'

select brand_id, item_type,sub_type
from product
where id = #{id}
